import { useState, useEffect } from "react";

const Photos = ({ title, setSearchClicked, searchClicked }) => {
  const [backendData, setBackendData] = useState([]);
  const [selectedPhotos, setSelectedPhotos] = useState([]);
  // console.log(props.title, props.setSearchClicked, props.searchClicked);

  // console.log(props);
  useEffect(() => {
    if (searchClicked) {
    // if (props && props.searchClicked) {
      // const title = props.title;
      fetch(`http://localhost:5001/searchImage?title=${title}`)
        .then((res) => res.json())
        .then((data) => {
          setBackendData(data);
          setSearchClicked(false);
        });
     }
  }, [title, searchClicked, setSearchClicked]);

  const selectedPhotosHandler = (id) => {
    const isSelected = selectedPhotos.includes(id);
    if (isSelected) {
      setSelectedPhotos(selectedPhotos.filter((photoId) => photoId !== id));
    } else {
      setSelectedPhotos([...selectedPhotos, id]);
    }
  };

  return (
    <div className=" photosContainer flex flex-row flex-wrap justify-center">
      {typeof backendData === "undefined" ? (
        <p>loading...</p>
      ) : (
        backendData.map((data, idx) => {
          return (
            <div key={idx}>
              <img src={data.imageUrl} alt={data.description} onClick={selectedPhotosHandler(data._id)} className="max-w-sm h-auto selection:border-red-500" />
            </div>
          );
          //   console.log(name);
        })
      )}
    </div>
  );
};

export default Photos;
