import { useState } from "react";
import Photos from "./Photos/Photos";

const SearchButton = () => {
  const [title, setTitle] = useState("");
  const [searchClicked, setSearchClicked] = useState(false);
  

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleButtonClick = () => {
    setSearchClicked(true);
  };

  return (
    <div className="py-1">
      <input
        type="text"
        placeholder="Enter search term"
        value={title}
        onChange={handleTitleChange}
        className="bg-green-50 border border-blue-500 text-black placeholder-cyan-900 text-sm rounded-lg focus:ring-green-500 focus:border-green-500 block w-full p-2.5 dark:bg-green-100 dark:border-green-400"
      />
      <button onClick={handleButtonClick} className="items-center bg-initial bg-gradient-to-t from-cyan-500 to-blue-500 rounded-3xl shadow-blue-500/50 font-serif content-center m-0 px-4  py-2 text-center hover:from-cyan-600 hover:to-blue-600 hover:scale-110 hover:transition-shadow hover:shadow-xl    ">Search</button>
      <Photos title={title} setSearchClicked={setSearchClicked} searchClicked={searchClicked} />
    </div>
  );
};

export default SearchButton;
