import { useState, useEffect } from "react";

const Photos = ({ title, setSearchClicked, searchClicked }) => {
  const [backendData, setBackendData] = useState([]);
  const [selectedPhotos, setSelectedPhotos] = useState([]);
  // console.log(props.title, props.setSearchClicked, props.searchClicked);

  // console.log(props);
  useEffect(() => {
    
    // if (props && props.searchClicked) {
      // const title = props.title;
      fetch(`http://localhost:5001/searchImage?title=${title}`)
        .then((res) => res.json())
        .then((data) => {
          setBackendData(data);
          setSearchClicked(false);
        });
     
  }, [searchClicked]);

  const selectedPhotosHandler = (idx) => {
    setSelectedPhotos(prevSelectedPhotos => {
      const photoIndex = prevSelectedPhotos.indexOf(idx);
      if (photoIndex !== -1) {
        // If the photo is already selected, remove it from the array
        return prevSelectedPhotos.filter((_, index) => index !== photoIndex);
      } else {
        // If the photo is not selected, add it to the array
        return [...prevSelectedPhotos, idx];
      }
      
    });
    console.log(selectedPhotos);
  };

  return (
    <div className=" photosContainer flex flex-row flex-wrap justify-center">
      {typeof backendData === "undefined" ? (
        <p>loading...</p>
      ) : (
        backendData.map((data, idx) => {
          return (
            <div key={idx}>
              <img src={data.imageUrl} alt={data.description} onClick={() => selectedPhotosHandler(idx)} className="max-w-sm h-auto hover:drop-shadow-2xl" />
            </div>
          );
          //   console.log(name);
        })
      )}
    </div>
  );
};

export default Photos;
